import 'package:get/get.dart';import 'package:todo_app/data/firebase/data_base_services.dart';import 'package:todo_app/data/models/task_model.dart';class TaskController extends GetxController {  var tasks = <TaskModel>[].obs;  @override  void onInit() {    super.onInit();    fetchTask();  } //fetchTask  void fetchTask() {    DataBaseServices.getTaskCollection().snapshots().listen((data) {      tasks.value = data.docs.map((doc) => doc.data()).toList();    });  }  //update Task Status  Future<void> updateTaskStatus(String taskId,      {bool? iSDone, DateTime? endTime}) async {    await DataBaseServices()        .updateTaskStatus(taskId: taskId, isDone: iSDone, endTime: endTime);  }  //addTask  Future<void> addTask(TaskModel task) async {    await DataBaseServices().addTask(taskModel: task);  }  //}